VALUES (0, 'none', 0, NULL, 1, NULL, '(Default Page Menu)', '', NULL, NULL, NULL, NULL, NULL, '', 0, NULL, 0, 0, 0, 4, NULL, NULL, NULL),
  (1, 'index.cfm', 2, 'topcats=1,onsale=1,new=1,hot=1,prodofday=1,notsold=1,listing=vertical', 1, 'home', 'home', 'Better than ever!', NULL, NULL, NULL, NULL, NULL, '<br />We&nbsp;have created this demo site to show off some of the&nbsp;incredible features of CFWebstore\'s new Fusebox Edition.&nbsp;This product is so flexible that it\'s impossible to&nbsp;demonstrate all the things it can do but&nbsp;look around&nbsp;and we\'re&nbsp;sure you\'ll agree that it is packed with incredible features! <br /><br />This page, like all the other pages in this site, is completely editable&nbsp;using&nbsp;any web browser. You have full control over the layout and content.', 1, NULL, 0, 7, 0, 0, NULL, NULL, NULL),
  (2, 'index.cfm?fuseaction=users.manager', 0, 'noline=1', 1, 'manager', 'my account', 'My Account', NULL, NULL, NULL, NULL, NULL, 'This text is defined in the page manager. I suppose it could come in handy for<br />\r\nannouncements. The title of the page and text in menu link are also editable.', 1, NULL, 0, 3, 23, 0, '', '', ''),
  (3, 'index.cfm?fuseaction=shopping.basket', 0, 'noline=1', 1, 'basket', 'view cart', '', NULL, NULL, NULL, NULL, NULL, '<blockquote style=\"margin-right: 0px;\" dir=\"ltr\" class=\"carttext\">\r\n<p><strong>Return Policy</strong>: All Sales Final. Return of damaged goods&nbsp;will be accepted at time of<br />\r\ndelivery with 15% restocking fee.</p>\r\n<p>This bottom text area is defined in the Page Manager for the shopping cart.</p>\r\n</blockquote>\r\n<p align=\"center\"><img width=\"39\" height=\"24\" border=\"0\" src=\"images/icons/cc_mastercard.gif\" alt=\"\" />&nbsp; <img width=\"36\" height=\"24\" border=\"0\" src=\"images/icons/cc_visa.gif\" alt=\"\" />&nbsp; <img width=\"29\" height=\"24\" border=\"0\" src=\"images/icons/cc_amex.gif\" alt=\"\" />&nbsp; <img width=\"60\" height=\"24\" border=\"0\" src=\"images/icons/cc_discover.gif\" alt=\"\" /></p>', 1, NULL, 0, 1, 23, 0, '', '', 'Shopping Cart'),
  (4, 'index.cfm?fuseaction=page.search', 5, NULL, 1, 'search', 'search', 'Site Search', NULL, NULL, NULL, NULL, NULL, 'Please enter keywords to search for:', 1, NULL, 0, 6, 0, 0, NULL, NULL, NULL),
  (5, 'index.cfm?fuseaction=page.searchresults', 6, NULL, 0, 'searchResults', 'Search Results', 'Search Results', NULL, NULL, NULL, NULL, NULL, '', 1, NULL, 0, 9999, 0, 0, NULL, NULL, NULL),
  (6, 'index.cfm?fuseaction=page.new', 3, 'new=1,onsale=1', 0, 'new', 'what\'s new', 'What\'s New', NULL, NULL, NULL, NULL, NULL, '<P>This&nbsp;page displays all the new and&nbsp;sale&nbsp;categories, products and feature articles.&nbsp;Items are automatically placed on this page when you&nbsp;turn on&nbsp;the \"new\" or \"highlight\" option&nbsp;on the item\'s admin page.&nbsp; </P>\r\n<P>This What\'s New page is included as a standard Page in the Page Manager. You can also call it as a Category template to include it as a Category&nbsp;OR it can be included as part of&nbsp; the Home page. </P>\r\n<P>You have the choice of selecting only New, only Sale, or Both.</P>', 1, NULL, 0, 9999, 0, 0, NULL, NULL, NULL),
  (7, 'index.cfm?fuseaction=page.membersOnly', 0, NULL, 0, 'membersOnly', 'Members Only', 'Members Only', NULL, NULL, NULL, NULL, NULL, '<p><strong><font color=\"#ff3300\">Sorry!&nbsp; A membership is required.</font></strong></p>\r\n<p>This text displays when content is locked with an access key and the user IS logged in and does not have the key. If the user was not logged in, the user would get a PLEASE LOG IN page instead.</p>\r\n<p>You can check your membership information by clicking on the Current Memberships link on the My Accounts page or clicking <a href=\"index.cfm?fuseaction=access.memberships\">HERE</a>.</p>\r\n<p>You can edit the text of this page in the Page Manager.</p>\r\n<p>This page automatically&nbsp;lists all&nbsp;Membership Products below.</p>', 1, NULL, 0, 9999, 0, 0, '', 'members, login', 'CFWebstore Members Only Page'),
  (8, 'index.cfm?fuseaction=page.contactUs', 15, 'noline=1,BoxTitle=Email Us', 1, 'contactUs', 'contact us', 'Contact Us', NULL, NULL, NULL, NULL, NULL, '<p>Have a question? Have a comment? Please contact us!</p>\r\n&nbsp;\r\n<table width=\"75%\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td>Phone:</td>\r\n            <td><strong>222-555-6442</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\">Mail:</td>\r\n            <td>\r\n            <p>CFWebstore<br />\r\n            Dogpatch Software<br />\r\n            222 Main Street<br />\r\n            Jonesville, MD</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>E-mail:</td>\r\n            <td><a href=\"mailto:info&#064;YourSite.com\">info-at-YourSite.com</a></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\nYou can also use the form below:\r\n<p>&nbsp;</p>', 1, NULL, 0, 4, 0, 0, '', '', ''),
  (9, 'index.cfm?fuseaction=page.sitemap', 14, 'noline=1,alpha=1', 1, 'sitemap', 'site map', 'Site Map', NULL, NULL, NULL, NULL, NULL, '<P>The following is a list of all the Categories, Products and Feature Articles on our site. We hope it will assist you in finding what you\'re looking for.</P>', 1, NULL, 0, 5, 13, 0, NULL, NULL, NULL),
  (10, 'index.cfm?fuseaction=shopping.wishlist', 0, NULL, 1, 'wishlist', 'wishlist', 'Wishlist', NULL, NULL, NULL, NULL, NULL, '', 1, NULL, 0, 2, 23, 0, NULL, NULL, NULL),
  (11, 'none', 0, NULL, 1, NULL, 'Related Links', '', NULL, NULL, NULL, NULL, NULL, '', 0, NULL, 0, 0, 11, 3, NULL, NULL, NULL),
  (12, 'index.cfm?fuseaction=page.onsale', 11, NULL, 1, 'onsale', 'Items on Sale', 'New & On Sale in the Store', NULL, NULL, NULL, NULL, NULL, '', 0, NULL, 2, 1, 13, 0, NULL, NULL, NULL),
  (13, 'index.cfm?fuseaction=page.custommenus', 0, 'noline=1', 1, 'custommenus', 'Custom Menus', 'Customize The Menus!', NULL, NULL, NULL, NULL, 3, '<p>You can build custom menus in the Page &amp; Menu Manager.</p>\r\n<p>Custom menu options can link to their own page of text&nbsp;(as this one does) or to any URL you specify either inside or outside the site.</p>\r\n<p>The Related Links menu shows how the Custom Menus can be used to link to an outside URL. We set these links to open in&nbsp;a new window.&nbsp;Also note that the &quot;Related Links&quot; title is not a link at all!</p>\r\n<p>The Custom Menus are completely sortable both at the group level and within each group.</p>\r\n<p>This page also shows the new DHTML menu features in CFWebstore<sup>&reg;</sup> version 6. Samples are included for both vertical and horizontal menus, but you can easily build a wide range of menus using the components included.</p>\r\n<p>&nbsp;Please note that while the menus are completely dynamic, they are by default saved in the user\'s session, so if you add store categories or pages to the demo and wish to see them on the menu, you will need to re-login for them to appear.</p>', 0, NULL, 0, 0, 13, 1, NULL, NULL, NULL),
  (14, 'http://www.macromedia.com', 0, NULL, 1, NULL, 'Cold Fusion', '', NULL, NULL, NULL, NULL, NULL, '', 0, 'target=\'_blank\'', 0, 2, 11, 0, NULL, NULL, NULL),
  (15, 'http://www.cfwebstore.com', 0, NULL, 1, NULL, 'CFWebstore', 'Link to CFWebstore', NULL, NULL, NULL, NULL, NULL, '<P>&nbsp;</P>', 0, 'target=\'_blank\'', 0, 1, 11, 0, NULL, NULL, NULL),
  (16, 'index.cfm?fuseaction=page.cvv2help', 0, 'noline=1', 0, 'cvv2help', 'cvv2help', 'Your Credit Card Verification Value', NULL, '', '', '', NULL, '<P>Entering the Card Verification Value 2 (CVV2) number helps us to prevent credit card fraud and keep costs down for everyone. Since a CVV2 number is listed on your credit card but is not stored anywhere, the only way to know the correct CVV2 number for your credit card is to physically have possession of the card itself. All VISA, MasterCard and American Express cards made in America&nbsp;have a CVV2 number. </P>\r\n<P><STRONG>How to find your CVV2 Number:</STRONG></P>\r\n<P><IMG height=115 alt=\"Amex Card\" src=\"images/icons/amex_cvv2.gif\" width=157 align=left border=0><STRONG>American Express</STRONG><BR><BR>Enter the 4 digit, non-embossed number printed above your account number on the <B>face</B> of your card.</P>\r\n<P>&nbsp;</P>\r\n<P><IMG height=115 src=\"images/icons/visa_cvv2.gif\" width=157 align=left border=0><STRONG>Visa</STRONG><BR><BR>Enter the 3-digit, non-embossed number printed on the signature panel on the <B>back</B> of the card immediately following the Visa card account number.</P>\r\n<P>&nbsp;</P>\r\n<P><IMG height=115 src=\"images/icons/mastercard_cvv2.gif\" width=157 align=left border=0><STRONG>MasterCard</STRONG><BR><BR>Enter the 3-digit, non-embossed number printed on the signature panel on the <B>back</B> of the card.</P>\r\n<P>&nbsp;</P>\r\n<P><IMG height=115 src=\"images/icons/discover_cvv2.gif\" width=157 align=left border=0><STRONG>Discover</STRONG><BR><BR>Enter the 3-digit, non-embossed number printed on the signature panel on the <B>back</B> of the card.</P>', 1, '', 0, 9999, 0, 0, NULL, NULL, NULL),
  (17, 'index.cfm?fuseaction=page.receipt', 0, 'noline=1', 0, 'receipt', 'receipt', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, NULL, 0, 9999, 0, 0, NULL, NULL, NULL),
